INSERT INTO `proses_bimbingan` (`id_proses`, `id_prodi`, `id_administrasi`, `tujuan_bim`, `pelaksanaan`, `pembicara`, `kendala`, `manfaat`, `ket_lain`, `sumber_data`, `id_sumber_data`, `lampiran`) VALUES
(2, 52, 3, '<p>Membantu mahasiswa untuk membangun kerangka berfikir dalam penulisan skripsi</p>', '<p style="text-align: justify;">Pembimbingan dilakukan minimal satu kali dalam seminggu, namun dalam pelaksanaan di dasarkan pada kesepakatan dosen dengan mahasiswa</p>', '<ol>\r\n<li>Masalah substansi</li>\r\n<li>Masalah metodologis</li>\r\n<li>Masalah teknis penulisan&nbsp;</li>\r\n</ol>', '<ol>\r\n<li>Kesulitan: mahasiswa mengalami hambatan dan sering tidak menghadiri proses pembimbingan</li>\r\n<li>Upaya mengatasi: menghubungi mahasiswa melalui surat dan telepon&nbsp;</li>\r\n</ol>', '<p>Mengasah kemampuan substansial dan metodologis</p>', '', '', NULL, ''),
(3, 50, 2, '<p style="text-align: justify;">1)&nbsp; Membantu mahasiswa menyusun program belajar secara&nbsp;lengkap sesuai dengan Program Studi yang diikuti&nbsp;dengan mempertimbangkan batas waktu studi pada program&nbsp;S-1 secara efisien, sehingga mahasiwa relatif tidak menemui kendala dalam mengikuti perkuliahan dan dalam proses&nbsp;pengerjaan tugas akhir.</p>\r\n<p style="text-align: justify;">2)&nbsp; Membantu mahasiswa menyusun rencana studi pada setiap awal semester sesuai dengan beban belajar serta minat dan kemampuan analitis mereka.</p>\r\n<p style="text-align: justify;">3) Menampung masalah-masalah akademik yang dihadapi&nbsp;mahasiswa dan memberikan masukan berupa solusi-solusi&nbsp;yang mungkin serta mekanisme pelaksanaannya</p>\r\n<p style="text-align: justify;">4) Mengikuti perkembangan studi mahasiswa dengan&nbsp;mengadakan pertemuan secara periodik, baik secara individual maupun kelompok untuk memecahkan kesulitan-kesulitan akademik.</p>\r\n<p style="text-align: justify;">5) Mendorong mahasiswa untuk bekerja keras dan giat belajar.</p>\r\n<p style="text-align: justify;">6)&nbsp; Memberikan rekomendasi bagi mahasiswa&nbsp;untuk kegiatan pertukaran pelajar atau magang.</p>\r\n<p style="text-align: justify;">7) Menyampaikan peringatan lisan maupun tulisan kepada mahasiswa yang berprestasi kurang dan atau menimbulkan masalah bagi kegiatan perkuliahan.</p>', '<p style="text-align: justify;">Pelaksanaan pembimbingan dapat dilaksanakan melalui temu muka secara periodik sesuai dengan jadwal yang telah disepakati dengan mahasiswa&nbsp;maupun dengan memanfaatkan teknologi informasi (media komunikasi, media sosial di Internet).</p>', '<p style="text-align: justify;">Topik-topik yang dapat dibicarakan dalam pembimbingan meliputi performa akademik hingga psikologi mahasiswa yang bersangkutan.&nbsp;</p>', '<p style="text-align: justify;">Kesulitan dalam pembimbingan dapat berupa mahasiswa yang tidak mau bekerja sama, kesulitan untuk menyesuaikan jadwal antara dosen dan mahasiswa, beban kerja dosen yang terkadang tidak memberikan waktu luang untuk berkomunikasi/berdiskusi dengan mahasiswa bimbingan, serta permasalahan pribadi mahasiswa yang tidak terpecahkan.</p>', '<p style="text-align: justify;">Manfaat utama dari kegiatan pembimbingan bermuara kepada efisiensi dan efektivitas pelaksanaan kegiatan perkuliahan. Hal ini dimungkinkan oleh pemantuan kondisi mahasiswa oleh dosen, sehingga masalah-masalah yang dihadapi oleh mahasiswa, terutama yang berkaitan dengan perkuliahan dapat diantisipasi dan atau diselesaikan dengan baik.&nbsp;</p>', '', '', NULL, ''),
(4, 54, 2, '<p>Menentukan rencana perkuliahan termasuk mata kuliah pilihan dan wajib yang akan diambil di semester mendatang oleh mahasiswa</p>', '<p>Mahasiswa menyerahkan Kartu Rencana Studi yang kemudian akan direview oleh Dosen Pembimbing Akademik. Selanjutnya, mahasiswa dan dosen bertemu untuk mendiskusikan lebih lanjut rencana perkuliahan</p>', '<p>Dosen Pembimbing Akademik</p>', '<p>Kendala-kendala mengikuti perkuliahan dan juga perkembangan studi mahasiswa di semester sebelumnya dan rencana-rencana yang akan dilakukan mahasiswa untuk meningkatkan performa studinya.</p>', '<p>Mahasiswa memiliki rencana studi dan kelulusan secara lebih jelas dan terkendali. Sehingga mereka dapat lulus tepat waktu.</p>', '', 'Borang Akreditasi jurusan Sosiologi tahun 2013', NULL, ''),
(5, 49, 3, '<ul>\r\n<li>Memastikan mahasiswa menempuh studinya dengan cara yang sistematis dan tepat waktu.</li>\r\n<li>Memastikan efektivitas proses pembelajaran yang bersangkutan.</li>\r\n</ul>', '<ul>\r\n<li>Secara rutin dilakukan menjelang masa pengisian KRS. Walaupun buku pedoman akademik telah memberikan panduan cukup lengkap, namun mahasiswa tetap berkonsultasi dengan dosen terkait pemilihan konsentrasi studi atau mata kuliah pilihan.</li>\r\n<li>Mahasiswa dapat sewaktu-waktu menemui dosen, terutama ketika menghadapi kesulitan-kesulitan terkait masalah akademik yang tak terduga waktunya.</li>\r\n</ul>', '<ul>\r\n<li>Penentuan/pemilihan konsentrasi studi</li>\r\n<li>Pemilihan mata kuliah pilihan</li>\r\n<li>Hambatan substantif, teknis maupun administratif terkait perkuliahan&nbsp;</li>\r\n</ul>', '<ul>\r\n<li>Penerapan sistem administrasi akademik <em>online </em>(portal akademik) berimplikasi kepada semakin berkurangnya frekuensi mahasiswa untuk bertemu dengan dosen pembimbing akademiknya.</li>\r\n<li>Dalam sistem yang sama, dosen dapat memantau secara <em>real time-online </em>kemajuan studi mahasiswa bimbingannya melalui sistem yang terkoneksi langsung dengan&nbsp; <em>database </em>administrasi akademik, sehingga dalam hal ini dosen diharapkan akan memiliki peran yang lebih proaktif dibanding mahasiswanya.</li>\r\n</ul>', '<ul>\r\n<li>Mahasiswa dapat memperoleh <em>expert judgement </em>&nbsp;yang dapat digunakan untuk membantu kelancaran proses studinya.</li>\r\n<li>Mahasiswa juga memiliki katup serta saluran untuk mencari solusi untuk problematika, termasuk persoalan personal yang potensial berdampak pada kinerja akademik yang bersangkutan.</li>\r\n</ul>', '', 'Borang 3A S1 MKP 2014', NULL, '');
